import React, { useState } from 'react';
import axios from 'axios';

const AccountPage = () => {
    const [companyId, setCompanyId] = useState('');
    const [profile, setProfile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const fetchProfile = async () => {
        setLoading(true);
        setError(null);

        try {
            const response = await axios.get(`http://localhost:8000/profiles/${companyId}`);
            setProfile(response.data);
        } catch (err) {
            setError('Error fetching profile');
        } finally {
            setLoading(false);
        }
    };

    const handleInputChange = (e) => {
        setCompanyId(e.target.value);
    };

    return (
        <div>
            <h1>Search Company Profile</h1>
            <input
                type="text"
                placeholder="Enter Company ID"
                value={companyId}
                onChange={handleInputChange}
                style={{ marginRight: '10px' }}
            />
            <button onClick={fetchProfile}>Fetch Profile</button>

            {loading && <p>Loading...</p>}
            {error && <p>{error}</p>}

            {profile && (
                <div style={{ border: '1px solid black', margin: '10px', padding: '10px' }}>
                    <h2>{profile.Name}</h2>
                    <p><strong>About:</strong> {profile.About}</p>
                    <p><strong>Contact Us:</strong> {profile.Hotline}</p>
                    <p><strong>Link:</strong> <a href={profile.Link} target="_blank" rel="noreferrer">{profile.Link}</a></p>
                </div>
            )}
        </div>
    );
};

export default AccountPage;
