import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ActivityForm = () => {
    const [activity, setActivity] = useState({
        date: '',
        time: '',
        location: '',
        price: '',
        category: '',
        discount: '',
        bookingOpen: false,
    });
    
    const [tags, setTags] = useState([]); // To hold the fetched tags
    const [selectedTags, setSelectedTags] = useState([]); // To hold selected tag IDs

    const navigate = useNavigate();

    // Fetch tags from the database
    useEffect(() => {
        const fetchTags = async () => {
            try {
                const response = await axios.get('http://localhost:8000/tags'); // Adjust the URL as necessary
                setTags(response.data); // Assuming response.data contains the list of tags
            } catch (error) {
                console.error('Error fetching tags:', error);
            }
        };

        fetchTags();
    }, []);

    const handleChange = (e) => {
        setActivity({
            ...activity,
            [e.target.name]: e.target.value,
        });
    };

    const handleTagChange = (e) => {
        const tagId = e.target.value;
        if (e.target.checked) {
            // Add the tag ID if checked
            setSelectedTags([...selectedTags, tagId]);
        } else {
            // Remove the tag ID if unchecked
            setSelectedTags(selectedTags.filter(id => id !== tagId));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const userId = localStorage.getItem('userId');
        console.log(userId);

        if (!userId) {
            console.error('User ID is missing. Cannot create activity.');
            return;
        }

        const activityData = {
            ...activity,
            tags: selectedTags, // Send selected tag IDs
            Advertiser: userId,
        };

        console.log('Sending activity data:', activityData); 

        try {
            const response = await axios.post('http://localhost:8000/newActivity', activityData);
            console.log('Activity created:', response.data);
            navigate('/');
        } catch (error) {
            console.error('Error creating activity:', error.response.data); // Log error response
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="date" name="date" value={activity.date} onChange={handleChange} placeholder="Date" required />
            <input type="time" name="time" value={activity.time} onChange={handleChange} placeholder="Time" required />
            <input type="text" name="location" value={activity.location} onChange={handleChange} placeholder="Location" required />
            <input type="text" name="price" value={activity.price} onChange={handleChange} placeholder="Price" required />
            <input type="text" name="category" value={activity.category} onChange={handleChange} placeholder="Category" required />
            <input type="text" name="discount" value={activity.discount} onChange={handleChange} placeholder="Special Discounts" />

            <div>
                <h4>Select Tags:</h4>
                {tags.map(tag => (
                    <label key={tag._id}>
                        <input 
                            type="checkbox" 
                            value={tag._id} 
                            onChange={handleTagChange} 
                        />
                        {tag.type} - {tag.historicPeriod} {/* Display tag information */}
                    </label>
                ))}
            </div>

            <label>
                Booking Open:
                <input type="checkbox" name="bookingOpen" checked={activity.bookingOpen} onChange={() => setActivity({ ...activity, bookingOpen: !activity.bookingOpen })} />
            </label>
            <button type="submit">Submit</button>
        </form>
    );
};

export default ActivityForm;
