import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AccountPage = () => {
    const [profile, setProfile] = useState(null); // It's a single profile, not an array
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Fetch profile by ID
        const fetchProfile = async () => {
            try {
                const response = await axios.get('http://localhost:8000/profiles/66fadb24907d47771aa57cd8');
                setProfile(response.data);  // Set the response data
                setLoading(false);
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };

        fetchProfile();
    }, []);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error: {error}</p>;

    // If no profile found, return a message
    if (!profile) return <p>No profile found</p>;

    return (
        <div>
            <h1>Account Profile</h1>
            <div style={{ border: '1px solid black', margin: '10px', padding: '10px' }}>
                <h2>{profile.Name}</h2>
                <p><strong>About:</strong> {profile.About}</p>
                <p><strong>Contact Us:</strong> {profile.Hotline}</p>
                <p><strong>Link:</strong> <a href={profile.Link} target="_blank" rel="noreferrer">{profile.Link}</a></p>
            </div>
        </div>
    );
};

export default AccountPage;
