import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import '../Components/Navbar.css';

const HomeScreen = () => {
    const [activities, setActivities] = useState([]);
    const [isAuthenticated, setIsAuthenticated] = useState(false); // State to track authentication

    useEffect(() => {
        const fetchActivities = async () => {
            try {
                const response = await axios.get('http://localhost:8000/activities');
                setActivities(response.data);
            } catch (error) {
                console.error('Error fetching activities:', error);
            }
        };

        fetchActivities();
    }, []);

    // This function can be modified based on how your authentication is set up
    const checkAuthentication = () => {
        // Here you can check if the user is signed in, e.g., via a token or user session
        // For demonstration, we'll toggle this manually. Replace this with your actual auth check.
        const userLoggedIn = localStorage.getItem('userLoggedIn'); // Replace with your actual logic
        setIsAuthenticated(userLoggedIn === 'true');
    };

    useEffect(() => {
        checkAuthentication();
    }, []);

    return (
        <div>
            <h1>Activities</h1>
            {activities.map(activity => (
                <div key={activity._id} style={{ border: '1px solid black', margin: '10px', padding: '10px' }}>
                    <h2>{activity.Category}</h2>
                    <p><strong>Date:</strong> {new Date(activity.Date).toLocaleDateString()}</p>
                    <p><strong>Time:</strong> {activity.Time}</p>
                    <p><strong>Location:</strong> {activity.Location.coordinates.join(', ')}</p>
                    <p><strong>Price:</strong> ${activity.Price}</p>
                    <p><strong>Discount:</strong> {activity.Discount}%</p>
                    <p><strong>Advertiser:</strong> {activity.Advertiser?.Name || 'Unknown Advertiser'}</p>
                </div>
            ))}

            {isAuthenticated && ( // Show button only if user is authenticated
                <Link to="/add-activity">
                    <button className='add-new-activity'>
                        Add New Activity
                    </button>
                </Link>
            )}
        </div>
    );
};

export default HomeScreen;
