import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './AccountPage.css'

const AccountPage = () => {
    const [profiles, setProfiles] = useState([]);
    const [selectedProfile, setSelectedProfile] = useState(null);
    const [editing, setEditing] = useState(false);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Fetch all profiles on component mount
    useEffect(() => {
        const fetchProfiles = async () => {
            try {
                const response = await axios.get('http://localhost:8000/profiles');
                setProfiles(response.data);
                setLoading(false);
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };

        fetchProfiles();
    }, []);

    // Fetch a single profile when clicked
    const fetchProfileDetails = async (id) => {
        setLoading(true);
        setError(null);
        try {
            const response = await axios.get(`http://localhost:8000/profiles/${id}`);
            setSelectedProfile(response.data);
            setEditing(false); // Reset editing state
            setLoading(false);
        } catch (err) {
            setError('Error fetching profile details');
            setLoading(false);
        }
    };

    // Handle editing form submission
    const handleUpdate = async () => {
        try {
            await axios.put(`http://localhost:8000/updateProfile/${selectedProfile._id}`, selectedProfile);
            setEditing(false);
        } catch (err) {
            setError('Error updating profile');
        }
    };

    // Handle form input changes
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setSelectedProfile({ ...selectedProfile, [name]: value });
    };

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error: {error}</p>;

    return (
        <div>
            <h1>Company Profiles</h1>

            {selectedProfile ? (
                <div>
                    <h2>{editing ? 'Edit Profile' : selectedProfile.Name}</h2>

                    {editing ? (
                        <div>
                            <input
                                type="text"
                                name="Name"
                                value={selectedProfile.Name}
                                onChange={handleInputChange}
                                placeholder="Company Name"
                            />
                            <textarea
                                name="About"
                                value={selectedProfile.About}
                                onChange={handleInputChange}
                                placeholder="About the Company"
                            />
                            <input
                                type="text"
                                name="Hotline"
                                value={selectedProfile.Hotline}
                                onChange={handleInputChange}
                                placeholder="Hotline"
                            />
                            <input
                                type="text"
                                name="Link"
                                value={selectedProfile.Link}
                                onChange={handleInputChange}
                                placeholder="Company Link"
                            />
                            <button onClick={handleUpdate}>Save Changes</button>
                        </div>
                    ) : (
                        <div>
                            <p><strong>About:</strong> {selectedProfile.About}</p>
                            <p><strong>Hotline:</strong> {selectedProfile.Hotline}</p>
                            <p><strong>Link:</strong> <a href={selectedProfile.Link} target="_blank" rel="noreferrer">{selectedProfile.Link}</a></p>
                            <button onClick={() => setEditing(true)}>Edit Profile</button>
                        </div>
                    )}

                    <button onClick={() => setSelectedProfile(null)}>Back to List</button>
                </div>
            ) : (
                <div className='profiles-display'>
                    {profiles.map((profile) => (
                        <div
                            key={profile._id}
                            style={{ border: '1px solid black', margin: '10px', padding: '10px' }}
                            onClick={() => fetchProfileDetails(profile._id)}
                        >
                            <h2>{profile.Name}</h2>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default AccountPage;
