import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const EditProfilePage = () => {
    const { id } = useParams();
    const navigate = useNavigate();

    const [profile, setProfile] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [field, setField] = useState('');
    const [newValue, setNewValue] = useState('');

    useEffect(() => {
        // Fetch the profile data
        const fetchProfile = async () => {
            try {
                const response = await axios.get(`http://localhost:8000/profiles/${id}`);
                setProfile(response.data);
                setLoading(false);
            } catch (err) {
                setError('Error fetching profile');
                setLoading(false);
            }
        };

        fetchProfile();
    }, [id]);

    const handleUpdate = async () => {
        if (!field || !newValue) {
            setError('Please select a field and enter a new value.');
            return;
        }

        try {
            const response = await axios.put(`http://localhost:8000/updateProfile/${id}`, {
                [field]: newValue, // Dynamically set the field to update
            });
            alert('Profile updated successfully!');
            navigate('/account'); // Redirect to account page after updating
        } catch (err) {
            setError('Error updating profile');
        }
    };

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div>
            <h1>Edit Company Profile</h1>
            <h2>{profile.Name}</h2>
            <div>
                <label htmlFor="field">Select Field to Update:</label>
                <select id="field" value={field} onChange={(e) => setField(e.target.value)}>
                    <option value="">Select a field</option>
                    <option value="Name">Name</option>
                    <option value="About">About</option>
                    <option value="Hotline">Hotline</option>
                    <option value="Link">Link</option>
                </select>
            </div>
            <div>
                <label htmlFor="newValue">New Value:</label>
                <input
                    type="text"
                    id="newValue"
                    value={newValue}
                    onChange={(e) => setNewValue(e.target.value)}
                />
            </div>
            <button onClick={handleUpdate}>Update</button>
        </div>
    );
};

export default EditProfilePage;
