import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const SignupPage = () => {
    const [formData, setFormData] = useState({
        Name: '',
        About: '',
        Hotline: '',
        Link: ''
    });
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        
        try {
            // Send POST request to create a new company profile
            const response = await axios.post('http://localhost:8000/createProfile', formData);
            const profileId = response.data.profile._id;
            
            // Redirect to the profile page using the profile ID
            navigate(`/profile/${profileId}`);
        } catch (err) {
            setError('Error signing up. Please try again.');
        }
    };

    return (
        <div>
            <h1>Company Signup</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="Name"
                    value={formData.Name}
                    onChange={handleInputChange}
                    placeholder="Company Name"
                    required
                />
                <textarea
                    name="About"
                    value={formData.About}
                    onChange={handleInputChange}
                    placeholder="About the Company"
                    required
                />
                <input
                    type="text"
                    name="Hotline"
                    value={formData.Hotline}
                    onChange={handleInputChange}
                    placeholder="Hotline"
                    required
                />
                <input
                    type="text"
                    name="Link"
                    value={formData.Link}
                    onChange={handleInputChange}
                    placeholder="Company Website"
                />
                <button type="submit">Sign Up</button>
            </form>

            {error && <p>{error}</p>}
        </div>
    );
};

export default SignupPage;
