import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import '../Components/Navbar.css';

const HomeScreen = () => {
    const [activities, setActivities] = useState([]);
    const [newActivity, setNewActivity] = useState({ 
        Category: '', 
        Date: '', 
        Time: '', 
        Location: { type: 'Point', coordinates: [0, 0] }, 
        Price: 0, 
        Discount: 0, 
        Tags: [] 
    });
    const userId = localStorage.getItem('userId');

    useEffect(() => {
        const fetchActivities = async () => {
            try {
                const response = await axios.get('http://localhost:8000/activities');
                setActivities(response.data);
            } catch (error) {
                console.error('Error fetching activities:', error);
            }
        };

        fetchActivities();
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewActivity((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    const handleLocationChange = (e) => {
        const { name, value } = e.target;
        setNewActivity((prev) => ({
            ...prev,
            Location: {
                ...prev.Location,
                coordinates: name === 'latitude' ? [value, prev.Location.coordinates[1]] : [prev.Location.coordinates[0], value]
            }
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:8000/newActivity', {
                ...newActivity,
                Advertiser: userId // Step 3: Include the user ID
            });
            // Optionally, you could also refetch activities or update the local state here
            alert('Activity created successfully');
            // Clear form after submission
            setNewActivity({ 
                Category: '', 
                Date: '', 
                Time: '', 
                Location: { type: 'Point', coordinates: [0, 0] }, 
                Price: 0, 
                Discount: 0, 
                Tags: [] 
            });
        } catch (error) {
            console.error('Error creating activity:', error);
        }
    };

    return (
        <div>
            <h1>Activities</h1>
            {activities.map(activity => (
                <div key={activity._id} style={{ border: '1px solid black', margin: '10px', padding: '10px' }}>
                    <h2>{activity.Category}</h2>
                    <p><strong>Date:</strong> {new Date(activity.Date).toLocaleDateString()}</p>
                    <p><strong>Time:</strong> {activity.Time}</p>
                    <p><strong>Location:</strong> {activity.Location.coordinates.join(', ')}</p>
                    <p><strong>Price:</strong> ${activity.Price}</p>
                    <p><strong>Discount:</strong> {activity.Discount}%</p>
                    <p><strong>Advertiser:</strong> {activity.Advertiser?.Name || 'Unknown Advertiser'}</p>
                </div>
            ))}

            {userId && (
                <form onSubmit={handleSubmit} style={{ marginTop: '20px' }}>
                    <h2>Add New Activity</h2>
                    <input type="text" name="Category" placeholder="Category" value={newActivity.Category} onChange={handleChange} required />
                    <input type="date" name="Date" value={newActivity.Date} onChange={handleChange} required />
                    <input type="text" name="Time" placeholder="Time" value={newActivity.Time} onChange={handleChange} required />
                    <input type="number" name="Price" placeholder="Price" value={newActivity.Price} onChange={handleChange} required />
                    <input type="number" name="Discount" placeholder="Discount" value={newActivity.Discount} onChange={handleChange} required />
                    <input type="number" name="latitude" placeholder="Latitude" onChange={handleLocationChange} required />
                    <input type="number" name="longitude" placeholder="Longitude" onChange={handleLocationChange} required />
                    <button type="submit">Create Activity</button>
                </form>
            )}
        </div>
    );
};

export default HomeScreen;
