import { useState, useRef } from "react";
import './styles.css';
// import Homeimg from './homeback.jpg';
// import flightimg from './jason-rosewell-P5aY_FocXAI-unsplash (1).jpg';
// import carrent from './carrent.jpg';
// import villa1 from './villa1.jpg';
// import villa4 from './donor1.jpg';

export default function HomePage() {
  const [destination, setDestination] = useState('');
  const [dates, setDates] = useState('');
  const [guests, setGuests] = useState('');
  const [tripSearch, setTripSearch] = useState('');
  const [tripFilter, setTripFilter] = useState('all');
  const [activitySearch, setActivitySearch] = useState('');
  const [activityFilter, setActivityFilter] = useState('all');

  const tripScrollRef = useRef(null);
  const activitiesScrollRef = useRef(null);

  const handleSearch = (e) => {
    e.preventDefault();
    console.log('Searching for:', { destination, dates, guests });
  };

  const handleTripSearch = (e) => {
    setTripSearch(e.target.value);
  };

  const handleTripFilter = (e) => {
    setTripFilter(e.target.value);
  };

  const handleActivitySearch = (e) => {
    setActivitySearch(e.target.value);
  };

  const handleActivityFilter = (e) => {
    setActivityFilter(e.target.value);
  };

  // Mock data for trips
  const trips = [
    { id: 1, name: "Luxury Villa", location: "Bali", type: "villa" },
    { id: 2, name: "City Apartment", location: "New York", type: "apartment"},
    { id: 3, name: "Beach House", location: "Maldives", type: "house" },
    { id: 4, name: "Beit omar seif", location: "Madinty", type: "house" },
  ];

  // Mock data for activities
  const activities = [
    { id: 1, name: "Scuba Diving", location: "Maldives", type: "activity" },
    { id: 2, name: "Mountain Trekking", location: "Nepal", type: "activity" },
    { id: 3, name: "City Tour", location: "Paris", type: "activity" },
    { id: 4, name: "Desert Safari", location: "Dubai", type: "activity" },
  ];

  // Filter and search trips
  const filteredTrips = trips.filter(trip => 
    (tripFilter === 'all' || trip.type === tripFilter) &&
    (trip.name.toLowerCase().includes(tripSearch.toLowerCase()) || 
     trip.location.toLowerCase().includes(tripSearch.toLowerCase()))
  );

  // Filter and search activities
  const filteredActivities = activities.filter(activity => 
    (activityFilter === 'all' || activity.type === activityFilter) &&
    (activity.name.toLowerCase().includes(activitySearch.toLowerCase()) || 
     activity.location.toLowerCase().includes(activitySearch.toLowerCase()))
  );

  // Scroll functions
  const scrollLeft = (ref) => {
    ref.current.scrollBy({ left: -300, behavior: 'smooth' });
  };

  const scrollRight = (ref) => {
    ref.current.scrollBy({ left: 300, behavior: 'smooth' });
  };

  return (
    <div className="min-h-screen flex flex-col">
      <header className="bg-white shadow-md">
        <nav className="container mx-auto px-9 py-9 flex justify-between items-center">
          <div className="text-2xl font-bold">WaterMelon Globe</div>
          <div className="hidden md:flex space-x-4">
            <a href="#" className="text-gray-600 hover:text-gray-900">Hotel</a>
            <a href="#" className="text-gray-600 hover:text-gray-900">Flight</a>
            <a href="#" className="text-gray-600 hover:text-gray-900">Train</a>
            <a href="#" className="text-gray-600 hover:text-gray-900">Travel</a>
            <a href="#" className="text-gray-600 hover:text-gray-900">Car Rental</a>
          </div>
          <div className="flex items-center space-x-2">
            <button className="px-9 py-1 border rounded">EN</button>
            <button className="px-9 py-1 border rounded">Log in</button>
            <button className="px-9 py-1 bg-blue-600 text-white rounded">Sign up</button>
          </div>
        </nav>
      </header>

      <main className="flex-grow">
        <section className="relative h-[600px] flex items-center justify-center text-center text-white">
          <div 
            className="absolute inset-0 bg-cover bg-center" 
          //  style={{backgroundImage: url(${Homeimg})}}
          ></div>
          <div className="absolute inset-0 bg-black bg-opacity-50"></div>
          <div className="relative z-10 max-w-4xl mx-auto px-4">
            <h1 className="text-4xl md:text-5xl font-bold mb-4">Discover Your Next Great Adventure</h1>
            <p className="text-xl mb-8">Immerse yourself in the wonders of the world with our handpicked selection of unforgettable journeys</p>
          </div>
        </section>

        <section className="container mx-auto px-4 -mt-16 relative z-20">
          <form onSubmit={handleSearch} className="bg-white rounded-lg shadow-lg p-6 flex flex-wrap gap-4">
            <div className="flex-1 min-w-[200px]">
              <label htmlFor="destination" className="block text-sm font-medium text-gray-700 mb-1">Find Location</label>
              <input
                type="text"
                id="destination"
                placeholder="Where are you going?"
                className="w-full p-2 border rounded"
                value={destination}
                onChange={(e) => setDestination(e.target.value)}
              />
            </div>
            <div className="flex-1 min-w-[200px]">
              <label htmlFor="dates" className="block text-sm font-medium text-gray-700 mb-1">Check in and Check out date</label>
              <input
                type="text"
                id="dates"
                placeholder="Add dates"
                className="w-full p-2 border rounded"
                value={dates}
                onChange={(e) => setDates(e.target.value)}
              />
            </div>
            <div className="flex-1 min-w-[200px]">
              <label htmlFor="guests" className="block text-sm font-medium text-gray-700 mb-1">Special Travels</label>
              <select
                id="guests"
                className="w-full p-2 border rounded"
                value={guests}
                onChange={(e) => setGuests(e.target.value)}
              >
                <option value="">Add guests</option>
                <option value="1">1 Guest</option>
                <option value="2">2 Guests</option>
                <option value="3">3 Guests</option>
                <option value="4">4+ Guests</option>
              </select>
            </div>
            <button type="submit" className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700 transition-colors">
              Search
            </button>
          </form>
        </section>

        <section className="container mx-auto px-4 py-12">
          <h2 className="text-2xl font-bold mb-6">Exclusive deals just for you!</h2>
          <div className="grid md:grid-cols-2 gap-6">
            <div className="bg-gray-100 rounded-lg overflow-hidden shadow-md">
              {/* <img src={flightimg} alt="Exclusive Flight Deal" className="w-full h-48 object-cover" /> */}
              <div className="p-4">
                <h3 className="font-bold text-lg mb-2">Exclusive Flight Deals Just For You!</h3>
                <p className="text-blue-600 font-bold">50% Off</p>
              </div>
            </div>
            <div className="bg-gray-100 rounded-lg overflow-hidden shadow-md">
              {/* <img src={carrent} alt="Exclusive Rental Deal" className="w-full h-48 object-cover" /> */}
              <div className="p-4">
                <h3 className="font-bold text-lg mb-2">Exclusive Rental Deals Just For You!</h3>
                <p className="text-blue-600 font-bold">25% Off</p>
              </div>
            </div>
          </div>
        </section>

        {/* Trips Section */}
        <section className="bg-gray-100 py-12">
          <div className="container mx-auto px-4">
            <h2 className="text-2xl font-bold mb-2">Top Trending Trips Nearby</h2>
            <p className="text-gray-600 mb-6">Discover the most unforgettable experiences your money can rent</p>
            <div className="mb-6 flex flex-wrap gap-4">
              <div className="flex-grow">
                <input
                  type="text"
                  placeholder="Search trips..."
                  value={tripSearch}
                  onChange={handleTripSearch}
                  className="w-full p-2 border rounded"
                />
              </div>
              <div>
                <select
                  value={tripFilter}
                  onChange={handleTripFilter}
                  className="w-full p-2 border rounded"
                >
                  <option value="all">All Types</option>
                  <option value="villa">Villas</option>
                  <option value="apartment">Apartments</option>
                  <option value="house">Houses</option>
                </select>
              </div>
            </div>

            {/* Horizontal Scrollable Trips */}
            <div className="relative flex items-center">
              <button 
                className="bg-gray-800 text-white p-2 rounded-l-md"
                onClick={() => scrollLeft(tripScrollRef)}
              >&#8592;</button>
              <div 
                ref={tripScrollRef} 
                className="overflow-x-auto whitespace-nowrap scroll-smooth flex gap-4"
                style={{ maxWidth: '100%' }} // Adjust for responsive design
              >
                {filteredTrips.map((trip) => (
                  <div key={trip.id} className="bg-white inline-block rounded-lg overflow-hidden shadow-md min-w-[200px]">
                    <img src={trip.image} alt={trip.name} className="w-full h-48 object-cover" />
                    <div className="p-4">
                      <h3 className="font-bold text-lg mb-1">{trip.name}</h3>
                      <p className="text-gray-600">{trip.location}</p>
                    </div>
                  </div>
                ))}
              </div>
              <button 
                className="bg-gray-800 text-white p-2 rounded-r-md"
                onClick={() => scrollRight(tripScrollRef)}
              >&#8594;</button>
            </div>
          </div>
        </section>

        {/* Activities Section */}
        <section className="container mx-auto px-4 py-12">
          <h2 className="text-2xl font-bold mb-2">Popular Activities</h2>
          <p className="text-gray-600 mb-6">Explore exciting activities that will make your trip unforgettable!</p>
          
          {/* Search and Filter for Activities */}
          <div className="mb-6 flex flex-wrap gap-4">
            <div className="flex-grow">
              <input
                type="text"
                placeholder="Search activities..."
                value={activitySearch}
                onChange={handleActivitySearch}
                className="w-full p-2 border rounded"
              />
            </div>
            <div>
              <select
                value={activityFilter}
                onChange={handleActivityFilter}
                className="w-full p-2 border rounded"
              >
                <option value="all">All Types</option>
                <option value="activity">Activities</option>
              </select>
            </div>
          </div>

          {/* Horizontal Scrollable Activities */}
          <div className="relative flex items-center">
            <button 
              className="bg-gray-800 text-white p-2 rounded-l-md"
              onClick={() => scrollLeft(activitiesScrollRef)}
            >&#8592;</button>
            <div 
              ref={activitiesScrollRef} 
              className="overflow-x-auto whitespace-nowrap scroll-smooth flex gap-4"
              style={{ maxWidth: '100%' }} // Adjust for responsive design
            >
              {filteredActivities.map((activity) => (
                <div key={activity.id} className="bg-white inline-block rounded-lg overflow-hidden shadow-md min-w-[200px]">
                  <img src={activity.image} alt={activity.name} className="w-full h-48 object-cover" />
                  <div className="p-4">
                    <h3 className="font-bold text-lg mb-1">{activity.name}</h3>
                    <p className="text-gray-600">{activity.location}</p>
                  </div>
                </div>
              ))}
            </div>
            <button 
              className="bg-gray-800 text-white p-2 rounded-r-md"
              onClick={() => scrollRight(activitiesScrollRef)}
            >&#8594;</button>
          </div>
        </section>
      </main>

      <footer className="bg-gray-800 text-white py-8">
        <div className="container mx-auto px-4">
          <div className="flex justify-center space-x-4 mb-4">
            <a href="#" className="hover:underline">Help Center</a>
            <a href="#" className="hover:underline">Privacy Policy</a>
            <a href="#" className="hover:underline">Terms of Service</a>
          </div>
          <p className="text-center text-sm">&copy; 2024 WaterMelon Globe. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
}
