import './SignUp.css';
import { useState } from "react";

const SignupTourist = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mobileNumber, setMobileNumber] = useState("");
  const [nationality, setNationality] = useState("");
  const [dob, setDOB] = useState("");
  const [status, setStatus] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const tourist = { username, email, password,mobileNumber,nationality,dob,status};
    
    const response = await fetch('/api/tourists', {
      method: 'POST',
      body: JSON.stringify(tourist),
      headers: {
        'Content-type': 'application/json'
      }
    });
    
    const json = await response; // Corrected this line

    // if (!response.ok) {
    //   setError(json.error); // Handle the error if response is not ok
    // }
    if (response.ok) {
      alert("Signing in as tourist was successful");
      setUsername('');
      setEmail('');
      setPassword('');
      setMobileNumber('');
      setNationality('');
      setDOB('');
      setStatus('');
      setError(null);
      console.log("New tourist was added", json);
    }
  };

  return (
    <form className="create" onSubmit={handleSubmit}>
      <h3>Sign up</h3>
      
      {error && <p className="error">{error}</p>} {/* Display error message if any */}

      <label> Username :
        <input 
          type="text"
          onChange={(e) => setUsername(e.target.value)}
          value={username}
        />
      </label>

      <label> Email :
        <input 
          type="email"
          onChange={(e) => setEmail(e.target.value)}
          value={email}
        />
      </label>

      <label> Password :
        <input 
          type="password"
          onChange={(e) => setPassword(e.target.value)}
          value={password}
        />
      </label>

      <label> Mobile Number :
        <input 
          type="text"
          onChange={(e) => setMobileNumber(e.target.value)}
          value={mobileNumber}
        />
      </label>

      <label> Nationality :
        <input 
          type="text"
          onChange={(e) => setNationality(e.target.value)}
          value={nationality}
        />
      </label>

      <label> DOB :
        <input 
          type="date"
          onChange={(e) => setDOB(e.target.value)}
          value={dob}
        />
      </label>

      <label> Job :
        <input 
          type="text"
          onChange={(e) => setStatus(e.target.value)}
          value={status}
        />
      </label>

      <button>Sign up as tourist</button>
    </form>
  );
};

export default SignupTourist;
