import { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom"; // Import for navigation
import './styles.css';

export default function HomePage() {
  const [destination, setDestination] = useState('');
  const [dates, setDates] = useState('');
  const [guests, setGuests] = useState('');
  const [tripSearch, setTripSearch] = useState('');
  const [tripFilter, setTripFilter] = useState('all');
  const [activitySearch, setActivitySearch] = useState('');
  const [activityFilter, setActivityFilter] = useState('all');
  const [activities, setActivities] = useState([]); // State for activities
  const [trips, setTrips] = useState([]); // State for trips
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error handling

  const tripScrollRef = useRef(null);
  const activitiesScrollRef = useRef(null);
  const navigate = useNavigate(); // For navigation

  useEffect(() => {
    const fetchActivities = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/getActivities'); // Adjust API URL
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setActivities(data); // Store the activities data in state
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    const fetchTrips = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/getTrips'); // Adjust API URL
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setTrips(data); // Store the trips data in state
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchActivities();
    fetchTrips();
  }, []);

  // Search handlers
  const handleTripSearch = (e) => {
    setTripSearch(e.target.value);
  };

  const handleActivitySearch = (e) => {
    setActivitySearch(e.target.value);
  };

  const handleTripClick = (tripId) => {
    navigate(`/trip/${tripId}`); // Navigate to trip details
  };

  const handleActivityClick = (activityId) => {
    navigate(`/activity/${activityId}`); // Navigate to activity details
  };

  // Filter and search trips
  const filteredTrips = trips.filter(trip => 
    (tripFilter === 'all' || trip.type === tripFilter) &&
    (trip.name.toLowerCase().includes(tripSearch.toLowerCase()) || 
     trip.location.toLowerCase().includes(tripSearch.toLowerCase()))
  );

  // Filter and search activities
  const filteredActivities = activities.filter(activity => 
    (activityFilter === 'all' || activity.type === activityFilter) &&
    (activity.name.toLowerCase().includes(activitySearch.toLowerCase()) || 
     activity.location.toLowerCase().includes(activitySearch.toLowerCase()))
  );

  // Scroll functions
  const scrollLeft = (ref) => {
    ref.current.scrollBy({ left: -300, behavior: 'smooth' });
  };

  const scrollRight = (ref) => {
    ref.current.scrollBy({ left: 300, behavior: 'smooth' });
  };

  // Handle loading and error states
  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="min-h-screen flex flex-col">
      <header className="bg-white shadow-md">
        <nav className="container mx-auto px-9 py-9 flex justify-between items-center">
          <div className="text-2xl font-bold">WaterMelon Globe</div>
          <div className="hidden md:flex space-x-4">
            <a href="#" className="text-gray-600 hover:text-gray-900">Hotel</a>
            <a href="#" className="text-gray-600 hover:text-gray-900">Flight</a>
            <a href="#" className="text-gray-600 hover:text-gray-900">Train</a>
            <a href="#" className="text-gray-600 hover:text-gray-900">Travel</a>
            <a href="#" className="text-gray-600 hover:text-gray-900">Car Rental</a>
          </div>
          <div className="flex items-center space-x-2">
            <button className="px-9 py-1 border rounded">EN</button>
            <button className="px-9 py-1 border rounded">Log in</button>
            <button className="px-9 py-1 bg-blue-600 text-white rounded">Sign up</button>
          </div>
        </nav>
      </header>

      <main className="flex-grow">
        <section className="container mx-auto px-4 py-12">
          <h2 className="text-2xl font-bold mb-2">Top Trending Trips Nearby</h2>
          <div className="mb-6 flex flex-wrap gap-4">
            <div className="flex-grow">
              <input
                type="text"
                placeholder="Search trips..."
                value={tripSearch}
                onChange={handleTripSearch}
                className="w-full p-2 border rounded"
              />
            </div>
            <div>
              <select
                value={tripFilter}
                onChange={handleTripFilter}
                className="w-full p-2 border rounded"
              >
                <option value="all">All Types</option>
                <option value="villa">Villas</option>
                <option value="apartment">Apartments</option>
                <option value="house">Houses</option>
              </select>
            </div>
          </div>

          {/* Horizontal Scrollable Trips */}
          <div className="relative flex items-center">
            <button 
              className="bg-gray-800 text-white p-2 rounded-l-md"
              onClick={() => scrollLeft(tripScrollRef)}
            >&#8592;</button>
            <div 
              ref={tripScrollRef} 
              className="overflow-x-auto whitespace-nowrap scroll-smooth flex gap-4"
              style={{ maxWidth: '100%' }} // Adjust for responsive design
            >
              {filteredTrips.map((trip) => (
                <div 
                  key={trip.id} 
                  className="bg-white inline-block rounded-lg overflow-hidden shadow-md min-w-[200px]"
                  onClick={() => handleTripClick(trip.id)} // Click to navigate
                >
                  <img src={trip.image} alt={trip.name} className="w-full h-48 object-cover" />
                  <div className="p-4">
                    <h3 className="font-bold text-lg mb-1">{trip.name}</h3>
                    <p className="text-gray-600">{trip.location}</p>
                  </div>
                </div>
              ))}
            </div>
            <button 
              className="bg-gray-800 text-white p-2 rounded-r-md"
              onClick={() => scrollRight(tripScrollRef)}
            >&#8594;</button>
          </div>
        </section>

        {/* Activities Section */}
        <section className="container mx-auto px-4 py-12">
          <h2 className="text-2xl font-bold mb-2">Popular Activities</h2>
          <div className="mb-6 flex flex-wrap gap-4">
            <div className="flex-grow">
              <input
                type="text"
                placeholder="Search activities..."
                value={activitySearch}
                onChange={handleActivitySearch}
                className="w-full p-2 border rounded"
              />
            </div>
            <div>
              <select
                value={activityFilter}
                onChange={handleActivityFilter}
                className="w-full p-2 border rounded"
              >
                <option value="all">All Types</option>
                <option value="activity">Activities</option>
              </select>
            </div>
          </div>

          {/* Horizontal Scrollable Activities */}
          <div className="relative flex items-center">
            <button 
              className="bg-gray-800 text-white p-2 rounded-l-md"
              onClick={() => scrollLeft(activitiesScrollRef)}
            >&#8592;</button>
            <div 
              ref={activitiesScrollRef} 
              className="overflow-x-auto whitespace-nowrap scroll-smooth flex gap-4"
              style={{ maxWidth: '100%' }} // Adjust for responsive design
            >
              {filteredActivities.map((activity) => (
                <div 
                  key={activity.id} 
                  className="bg-white inline-block rounded-lg overflow-hidden shadow-md min-w-[200px]"
                  onClick={() => handleActivityClick(activity.id)} // Click to navigate
                >
                  <img src={activity.image} alt={activity.name} className="w-full h-48 object-cover" />
                  <div className="p-4">
                    <h3 className="font-bold text-lg mb-1">{activity.name}</h3>
                    <p className="text-gray-600">{activity.location}</p>
                  </div>
                </div>
              ))}
            </div>
            <button 
              className="bg-gray-800 text-white p-2 rounded-r-md"
              onClick={() => scrollRight(activitiesScrollRef)}
            >&#8594;</button>
          </div>
        </section>
      </main>
    </div>
  );
}
