const TouristDetails = ({ tourist }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [updatedTourist, setUpdatedTourist] = useState({ ...tourist });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedTourist({ ...updatedTourist, [name]: value });
  };

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleConfirmClick = () => {
    // Here you would call the API to update the tourist data
    // Assuming updateTourist is a function that handles the API call
    updateTourist(updatedTourist)
      .then(() => {
        alert("Your profile has been successfully updated!");
        setIsEditing(false);
      })
      .catch((error) => {
        alert("An error occurred while updating your profile.");
      });
  };

  const updateTourist = async (updatedTourist) => {
    // Replace this with your actual API call logic
    return fetch("/api/updateTourist", {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(updatedTourist),
    });
  };

  return (
    <div className="tourist-details">
      {!isEditing ? (
        <>
          <strong>Username: </strong>
          {tourist.username}
          <p>
            <strong>Email: </strong>
            {tourist.email}
          </p>
          <p>
            <strong>Password: </strong>
            {tourist.password}
          </p>
          <p>
            <strong>Mobile Number: </strong>
            {tourist.mobileNumber}
          </p>
          <p>
            <strong>Nationality: </strong>
            {tourist.nationality}
          </p>
          <p>
            <strong>Date of Birth: </strong>
            {tourist.dob}
          </p>
          <p>
            <strong>Status: </strong>
            {tourist.status}
          </p>
          <p>
            <strong>Wallet: </strong>
            {tourist.wallet}
          </p>

          <p>{tourist.createdAt}</p>

          <button onClick={handleEditClick}>Update Profile</button>
        </>
      ) : (
        <>
          <p>
            <strong>Username: </strong>
            <input
              type="text"
              name="username"
              value={updatedTourist.username}
              onChange={handleInputChange}
            />
          </p>
          <p>
            <strong>Email: </strong>
            <input
              type="email"
              name="email"
              value={updatedTourist.email}
              onChange={handleInputChange}
            />
          </p>
          <p>
            <strong>Password: </strong>
            <input
              type="password"
              name="password"
              value={updatedTourist.password}
              onChange={handleInputChange}
            />
          </p>
          <p>
            <strong>Mobile Number: </strong>
            <input
              type="text"
              name="mobileNumber"
              value={updatedTourist.mobileNumber}
              onChange={handleInputChange}
            />
          </p>
          <p>
            <strong>Nationality: </strong>
            <input
              type="text"
              name="nationality"
              value={updatedTourist.nationality}
              onChange={handleInputChange}
            />
          </p>
          <p>
            <strong>Date of Birth: </strong>
            {tourist.dob} {/* Keeping Date of Birth as text */}
          </p>
          <p>
            <strong>Status: </strong>
            <input
              type="text"
              name="status"
              value={updatedTourist.status}
              onChange={handleInputChange}
            />
          </p>
          <p>
            <strong>Wallet: </strong>
            {tourist.wallet} {/* Keeping Wallet as text */}
          </p>

          <p>{tourist.createdAt}</p>

          <button onClick={handleConfirmClick}>Confirm</button>
        </>
      )}
    </div>
  );};
export default TouristDetails;
